insert  into `novedades`(`id`,`title`,`resumen`,`cuerpo`,`textoDestacado`,`fecha`,`publicado`,`video`,`videoDuration`) values (1,'Jornadas de Conciencia y Energ&iacute;a','<p>4 y 5 de mayo:&nbsp;Deepak Chopra, Caroline Myss y Ric Weinman en el Teatro San Mart&iacute;n. Compr&aacute; tus entradas <a href=\"http://www.livepass.com.ar/evento/765/jornadas-de-conciencia-y-energia\" target=\"_blank\">aqu&iacute;</a>.</p>\n','<p><u>4 de mayo: Jornada de Conciencia</u></p>\n\n<p><strong>Deepak Chopra</strong>, conferencista y escritor prol&iacute;fico sobre espiritualidad y el poder de la mente en la curaci&oacute;n m&eacute;dica, abrir&aacute; y cerrar&aacute; el encuentro.</p>\n\n<p>Durante la jornada habr&aacute; conferencias y talleres vivenciales sobre la ampliaci&oacute;n de la conciencia, impartidos por expertos nacionales, quienes a su vez forman parte de la Fundaci&oacute;n Columbia.</p>\n\n<p><strong>Horario: de 10 a 20.30 hs</strong></p>\n\n<p><u>5 de mayo, Jornada de Energ&iacute;a </u></p>\n\n<p>El tema central ser&aacute;n las Terapias Energ&eacute;ticas. Principales conferencistas:</p>\n\n<p>- <strong>Caroline Myss</strong>,&nbsp;autora y disertante de reputaci&oacute;n internacional en los temas de espiritualidad, conciencia, salud y medicina energ&eacute;tica.</p>\n\n<p>- <strong>Ric Weinman</strong>, fundador de Vortex Healing, sistema hol&iacute;stico de sanaci&oacute;n energ&eacute;tico.</p>\n\n<p>Durante la jornada habr&aacute; talleres pr&aacute;cticos, facilitados por expertos nacionales, quienes a su vez son parte de la Fundaci&oacute;n Columbia.</p>\n\n<p><strong>Horario: de 10 a 18 hs</strong></p>\n\n<p>Puntos de venta <a href=\"http://www.livepass.com.ar/evento/765/jornadas-de-conciencia-y-energia\" target=\"_blank\">aqu&iacute;.</a></p>\n','<p><strong>Deepak Chopra</strong>, estar&aacute; en Buenos Aires en las <strong>Jornadas de Conciencia y Energ&iacute;a</strong>, organizadas por la <strong>Fundaci&oacute;n Columbia</strong> con motivo de su lanzamiento internacional.&nbsp;El evento de dos d&iacute;as de duraci&oacute;n cuenta con el apoyo del <a target=\"_blank\">Omega Institute</a>&nbsp;y tendr&aacute; la participaci&oacute;n de destacadas personalidades del mundo en los temas de ampliaci&oacute;n de la Conciencia y Terapias Energ&eacute;ticas como <strong>Caroline Myss y</strong> <strong>Ric Weinman.&nbsp;</strong>Las jornadas contar&aacute;n con destacados conferencistas nacionales y facilitadores de talleres vivenciales.&nbsp;</p>\n','2012-11-30',1,'',0),(2,'Inauguraci&oacute;n de Nuestra Casa','<p>En el mes de abril Fundaci&oacute;n Columbia&nbsp;inaugura su sede en el&nbsp;barrio de Palermo, un espacio para vos,&nbsp;donde iniciar un camino de transformaci&oacute;n personal o continuarlo profundizando conocimientos y experiencias.&nbsp;</p>\n','<p>Nuestra casa, ubicada en Jorge Luis Borges 2020, tendrá cinco salas para actividades en simultáneo, un salón de usos múltiples, una terraza y un jardín en los que podrás compartir experiencias, conocer gente afín e ir formando un grupo de pertenencia según tus inquietudes. Habrá también un café-bar y, en nuestra biblioteca, encontrarás un espacio para el silencio, la reflexión y la lectura.</p><p>Además de nuestros cursos, talleres y seminarios regulares a cargo de los idóneos&nbsp;profesionales en cada una de las disciplinas, habrá clases abiertas, conciertos y performances diversas. ¡Te esperamos!</p>','<p>En el mes de abril Fundaci&oacute;n Columbia&nbsp;inaugura su sede en el neur&aacute;lgico barrio de Palermo, un espacio para vos,&nbsp;<strong>tu lugar</strong>&nbsp;donde iniciar un camino de transformaci&oacute;n personal o continuarlo profundizando conocimientos y experiencias. &nbsp;</p>\n\n<p><strong>Las inscripciones&nbsp;abrir&aacute;n&nbsp;en febrero.</strong></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n','2012-12-01',1,'',0);

