/* Function  structure for function  `strip_tags` */

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `strip_tags`($str TEXT, $maxLength int) RETURNS text CHARSET latin1
    DETERMINISTIC
BEGIN
    DECLARE $START, $END INT DEFAULT 1;
    declare $RET text;
    label: LOOP
        SET $START = LOCATE("<", $str, $START);
        IF (!$START) THEN leave label; END IF;
        SET $END = LOCATE(">", $str, $START);
        IF (!$END) THEN SET $END = $START; END IF;
        SET $str = INSERT($str, $START, $END - $START + 1, "");
    END LOOP;
    
	set $str = CONCAT(SUBSTRING($str, 1, $maxLength), '...');
	return $str;
    
END */$$
DELIMITER ;

